  function examList(list){
    let examTrue = false;
    // Проверяю есть ли у листов актив
    list.forEach(item => {
      if(item.querySelector('.grop-okey').classList.contains('active')){
        examTrue = true;
      }
    });
    // Если есть актив убираю блок с кнопки
    if(examTrue === true){ 
      btnGener.style.pointerEvents = 'auto'
      btnGener.style.color = 'rgba(0, 0, 0, .82)'

    }else{
      btnGener.style.pointerEvents = 'none'
      btnGener.style.color = 'rgba(0, 0, 0, .42)'
    }


    return examTrue;
  }

  function shuffle(array) {
    for (let i = array.length - 1; i > 0; i--) {
      let j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  }
  
