  listChild.forEach((item) => {
    // При нажатие на лист добавляю ксласс актив этому листу
    item.querySelector('.icon-generator').addEventListener('click', ()=> {
      if(item.querySelector('.grop-okey').classList.contains('active')){
        item.querySelector('.grop-okey').classList.remove('active');
        item.querySelector('.input-generator').classList.remove('active');
      }else{
        item.querySelector('.grop-okey').classList.add('active');
        item.querySelector('.input-generator').classList.add('active');
      }
      examList(listChild)
    })
  })


  btnGener.addEventListener('click', () => {
    const btnBacked = btnBack();
    
    bodyGener.append(btnBacked);
    
    const arrayWorld = [];

    let radiCheck = null;

    radioGener.forEach(item => {
      if(item.checked){
        radiCheck = item.value;
      }
    })



    listChild.forEach((item) => {
      if(item.querySelector('.grop-okey').classList.contains('active')){
        if(item.querySelector(".input-generator").name == 'down'){
          arrayWorld.push({name: 'Нижний мир', number: item.querySelector(".input-generator").value, vidios: arrDownWorld})
        }
        if(item.querySelector(".input-generator").name == 'center'){
          arrayWorld.push({name: 'Средний мир', number: item.querySelector(".input-generator").value, vidios: arrCenterWorld})
        }
        if(item.querySelector(".input-generator").name == 'uper'){
          arrayWorld.push({name: 'Верхний мир', number: item.querySelector(".input-generator").value, vidios: arrUperWorld})
        }
        if(item.querySelector(".input-generator").name == 'allworld'){
          arrayWorld.push({name: 'Все миры', number: item.querySelector(".input-generator").value, vidios: arrAllWorld})
        }
        
      }
    })
    
    arrayWorld.forEach(item => {
      if(item.name !== 'Все миры' && (item.number === '' || Number(item.number) < 1 || Number(item.number) > 9)){
        item.number = '9';
      }

      if(item.number === '' || Number(item.number) > 27 || Number(item.number) < 1){
        item.number = '27';
      }
    })


    listGener.style.display = 'none';
    groupBtnGener.style.display = 'none';
    
    
    
    arrayWorld.forEach( item => {
      const arrVideosText = [];
      const list = createList();
      const headerGener = headerCreate(item.name);
      console.log(item)
      container.append(headerGener)
      container.append(list)

      if(checkGener.checked === true){
        item.vidios.forEach((itemArr,i,arr) => {
          arrVideosText.push(itemArr)
        })
        shuffle(arrVideosText);
        for(let i in arrVideosText){
          arrVideosText.splice(Number(item.number))
        }
      }
      
      if(checkGener.checked !== true){
        item.vidios.forEach((itemArr) => {
          arrVideosText.push(itemArr)
          arrVideosText.splice(Number(item.number))
        })
      }

      for(let i of arrVideosText){
        if(radiCheck === 'true'){
          const listItem = createItem(i.photo, null , null, i.text, radiCheck, i.name, i.video);
          list.append(listItem.item)
        }else{
          let listItem = createItem(i.photo, i.photo2, i.photo3, i.text, radiCheck, i.name, null);
          list.append(listItem.item)
        }
      }
      arrVideosText.splice(0)
    })

    
    btnBacked.addEventListener('click', (e) => {
      const listRemove = container.querySelectorAll('.world-class')
      const headerRemve = container.querySelectorAll('.header-gener')
      const btnBack = bodyGener.querySelector('.btn-back')
 
      listRemove.forEach(item => {
        item.remove();
      })
      headerRemve.forEach(item => {
        item.remove();
      })
      btnBack.remove();
      listGener.style.display = 'flex';
      groupBtnGener.style.display = 'flex';
    })
    
  })
